"use strict";

function comment() {
    new Swiper(".section-slide .swiper-container", {
        slidesPerView: 3,
        spaceBetween: 30,
        navigation: {
            nextEl: ".section-slide .swiper-button-next",
            prevEl: ".section-slide .swiper-button-prev",
            type: "bullets",
            clickable: !0
        },
        breakpoints: {
            1024: {
                slidesPerView: 3,
                spaceBetween: 10
            },
            768: {
                slidesPerView: 2,
                spaceBetween: 10
            },
            575: {
                slidesPerView: 1,
                spaceBetween: 0
            }
        }
    })
}
function popup(){
    $(".show").on("click", function(e) {
        e.preventDefault();
        $("." + $(this).data("pop")).fadeIn(400);
    });
    $(".popup").on("click", function() {
        $(this).fadeOut(400);
    });

    $(".close").on("click", function() {
        $(this).parentsUntil(".popup").parent().fadeOut(400);
    });

    $(document).on("click", function(e) {
        if(e.keyCode === 27) {
            e.preventDefault();
            $(".popup").fadeOut(400);
        }
    });

    $(".popup .alert").on("click", function(e) {
        e.stopPropagation();
    });
}

document.addEventListener("DOMContentLoaded", function () {
    comment();
    popup();
});
